import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

// 0: show trang khách hàng
// 1: show trang giỏ hàng
// 2: show trang cá nhân khách hàng

const initialState = {
  value: 0, 
  status: 'idle',
};

export const khachHangSlice = createSlice({
  name: 'cooker',
  initialState,
 
  reducers: {
    showMenu: (state) => {
      state.value = 1;
    },
    showOrder: (state) => {
      state.value = 0;
    },
  },
});

export const { showMenu, showOrder } = khachHangSlice.actions;

export const selectShow = (state) => state.counter2.value;

export default cookSlice.reducer;
