import {useState} from 'react'
import styled from 'styled-components'

const c = styled.td`
    display: flex;
`

const Row = () => {
    const [amount, setAmount] = useState(1)
    return (
        <tr>
            <td style={{width: "50%"}}>Banh xeo viet nam</td>
            <td style={{width: "25%"}}>
                <span onClick={() => setAmount(amount - 1)}>-</span>
                {amount}
                <span onClick={() => setAmount(amount + 1)}>+</span>
            </td>
            <td>{100000 * amount} vnd</td>
        </tr>
    )
}

export default Row
